import React from "react";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { postPokemon, getTipos } from "../actions/index.js";
import { useDispatch, useSelector } from "react-redux";

export default function PokemonCreate() {
  const dispatch = useDispatch();

  let estadoTipos = useSelector(
    (state) => state.arrayTipos
  ); /*VERIFICAR SI ES tipo, tipos o estadoTipos*/
  console.log(estadoTipos);
  const [input, setInput] = useState({
    name: "",
    hp: "",
    attack: "",
    defense: "",
    speed: "",
    height: "",
    weight: "",
    image:
      "https://forums.pokemmo.eu/uploads/monthly_2020_10/1392966187789.gif.8f8685345a400e0e5d6ca3c2a1aba734.gif",
    tipo: [],
  });

  function handleChange(e) {
    setInput({
      ...input,
      [e.target.name]: e.target.value,
    });
    console.log(input);
  }

  function handleSelect(e) {
    setInput({
      ...input,
      tipo: [...input.tipo, e.target.value],
    });
    console.log(input);
  }

  function handleDelete(e, t) {
    e.preventDefault();
    setInput({
      ...input,
      tipo: input.tipo.filter((el) => el !== t),
    });
  }

  function handleSubmit(e) {
    e.preventDefault();
    console.log(input);
    dispatch(postPokemon(input));
    alert("Pokemon Creado!!");
    setInput({
      name: "",
      hp: "",
      attack: "",
      defense: "",
      speed: "",
      height: "",
      weight: "",
      image: "",
      tipo: [],
    });
  }

  useEffect(() => {
    dispatch(getTipos());
  }, []);

  return (
    <div>
      <Link to="/home">
        {" "}
        <button>Home</button>{" "}
      </Link>

      <h1>Crea tu Pokemon!</h1>

      <form onSubmit={(e) => handleSubmit(e)}>
        <div>
          <label>NOMBRE:</label>
          <input
            type="text"
            pattern="[a-zA-Z ]{2,254}"
            value={input.name}
            name="name"
            onChange={(e) => handleChange(e)}
            placeholder="Nombre..."
            required
            pattern="[a-zA-Z]+"
            minlength="4"
            maxlength="15"
            title="Solo texto sin números"
          />
        </div>
        <div>
          <label>HP:</label>
          <input
            type="number"
            value={input.hp}
            name="hp"
            onChange={(e) => handleChange(e)}
            placeholder="Hit Points..."
            required
            minlength="1"
            maxlength="3"
          />
        </div>
        <div>
          <label>FUERZA:</label>
          <input
            type="number"
            value={input.attack}
            name="attack"
            onChange={(e) => handleChange(e)}
            placeholder="Fuerza..."
            required
            minlength="1"
            maxlength="3"
          />
        </div>
        <div>
          <label>DEFENSA:</label>
          <input
            type="number"
            value={input.defense}
            name="defense"
            onChange={(e) => handleChange(e)}
            placeholder="Defensa..."
            required
            minlength="1"
            maxlength="3"
          />
        </div>
        <div>
          <label>VELOCIDAD:</label>
          <input
            type="number"
            value={input.speed}
            name="speed"
            onChange={(e) => handleChange(e)}
            placeholder="Velocidad..."
            required
            minlength="1"
            maxlength="3"
          />
        </div>
        <div>
          <label>ALTURA:</label>
          <input
            type="number"
            step="any"
            value={input.height}
            name="height"
            onChange={(e) => handleChange(e)}
            placeholder="Altura..."
            minlength="1"
            maxlength="3"
          />
        </div>
        <div>
          <label>PESO:</label>
          <input
            type="number"
            step="any"
            value={input.weight}
            name="weight"
            onChange={(e) => handleChange(e)}
            placeholder="Peso..."
            required
            minlength="1"
            maxlength="3"
          />
        </div>
        <label>TIPO:</label>
        <select required onChange={(e) => handleSelect(e)}>
          <option value="none" selected disabled hidden>
            Selecciona Tipo(s)
          </option>
          {estadoTipos?.map((t) => (
            <option value={t.name}>{t.name}</option>
          ))}
        </select>
        {input.tipo?.map((e, t) => (
          <div>
            <p>{e}</p>
            <button onClick={(e) => handleDelete(e, t)}> X </button>
          </div>
        ))}
      </form>
      <button type="submit">Crear Pokemon</button>
    </div>
  );
}
