/* eslint-disable jsx-a11y/img-redundant-alt */
import React from "react";

export default function Card({ name, image, tipo }) {
  return (
    <div>
      <img
        src={
          image
            ? image
            : "https://th.bing.com/th/id/OIP.hiEgh9BQPD-qzMRGDDxLIwHaGL?w=211&h=180&c=7&r=0&o=5&dpr=1.25&pid=1.7"
        }
        alt="iMAGEN nOT fOUND"
        width="80px"
        height="70px"
      />
      <h3>{name ? name : "POKEMON NO ENCONTRADO"}</h3>
      <h5>{tipo}</h5>
    </div>
  );
}
