import React from 'react';
import { useState, useEffect } from 'react';
import { Link, useHistory } from 'react-router-dom';
import { postPokemon, getTipos } from '../actions/index.js';
import { useDispatch, useSelector } from 'react-redux';

export default function PokemonCreate(){
  const dispatch = useDispatch();
  let estadoTipos = useSelector((state) => state.arrayTipos) /*VERIFICAR SI ES tipo, tipos o estadoTipos*/
console.log(estadoTipos);
  const [input, setInput] = useState({
    name: '',
    hp: '',
    attack: '',
    defense: '',
    speed: '',
    height: '',
    weight: '',
    image: '',
    tipo: []
  })

  function handleChange(e){
    setInput({
      ...input,
      [e.target.name] : e.target.value
    })
  }

  function handleSelect(e){
    setInput({
      ...input,
      tipo: [...input.estadoTipos, e.target.value]

    })
    console.log(name)

  }

  useEffect(() => {
    dispatch(getTipos());
  }, [])

  return (
    <div>
      <Link to='/home'> <button>Home</button> </Link>

      <h1>Crea tu Pokemon!</h1>

      <form>
        <div>
          <label>NOMBRE:</label>
          <input
            type= 'text'
            value= {input.name}
            name= 'name'
            onChange={handleChange}
          />
        </div>

        <div>
          <label>HP:</label>
          <input
            type= 'number'
            value= {input.hp}
            name= 'hp'
            onChange={handleChange}
          />
        </div>

        <div>
          <label>FUERZA:</label>
          <input
            type= 'number'
            value= {input.attack}
            name= 'attack'
            onChange={handleChange}
          />
        </div>

        <div>
          <label>DEFENSA:</label>
          <input
            type= 'number'
            value= {input.defense}
            name= 'defense'
            onChange={handleChange}
          />
        </div>

        <div>
          <label>VELOCIDAD:</label>
          <input
            type= 'number'
            value= {input.speed}
            name= 'speed'
            onChange={handleChange}
          />
        </div>

        <div>
          <label>ALTURA:</label>
          <input
            type= 'number'
            step="any"
            value= {input.height}
            name= 'height'
            onChange={handleChange}
          />
        </div>

        <div>
          <label>PESO:</label>
          <input
            type= 'number'
            step="any"
            value= {input.weight}
            name= 'weight'
            onChange={handleChange}
          />
        </div>

          <label>TIPO:</label>
          <select onChange={(e) => handleSelect(e)}>
            {estadoTipos?.map((t) => (<option value={t.name}>{t.name}</option>) )}
          </select>
          <div>
          <ul><li>{input.estadoTipos?.map(el => el + " ,")}</li></ul>
          <button type='submit'>Crear Pokemon</button>
          </div>

      </form>
    </div>
  )
}
